import { BigNumber } from "ethers/utils";
import { Address } from "./simple-types";
export declare enum AssetType {
    ETH = 0,
    ERC20 = 1
}
export declare type Terms = {
    assetType: AssetType;
    limit: BigNumber;
    token: Address;
};
export declare type Transaction = {
    assetType: AssetType;
    limit: BigNumber;
    token?: Address;
    to: Address[];
    value: BigNumber[];
    data: string[];
};
export declare type AppIdentity = {
    owner: string;
    signingKeys: string[];
    appDefinitionAddress: string;
    termsHash: string;
    defaultTimeout: number;
};
export declare type AppInterface = {
    addr: string;
    stateEncoding: string;
    actionEncoding: string | undefined;
};
export declare type SignedStateHashUpdate = {
    stateHash: string;
    nonce: number;
    timeout: number;
    signatures: string;
};
export declare type ETHBucketAppState = {
    alice: string;
    bob: string;
    aliceBalance: BigNumber;
    bobBalance: BigNumber;
};
